import React, {useState} from 'react';

import Card from '../UI/Card';
import classes from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';
import useHttp from '../../hooks/use-http';

const DUMMY_MEALS = [
    {
        id: 'm1',
        name: 'Sushi',
        description: 'Finest fish and veggies',
        price: 22.99,
    },
    {
        id: 'm2',
        name: 'Schnitzel',
        description: 'A german specialty!',
        price: 16.5,
    },
    {
        id: 'm3',
        name: 'Barbecue Burger',
        description: 'American, raw, meaty',
        price: 12.99,
    },
    {
        id: 'm4',
        name: 'Green Bowl',
        description: 'Healthy...and green...',
        price: 18.99,
    },
];

const AvailableMeals = () => {
    const [meals, setMeals] = useState([]);

    const {isLoading, error, sendRequest: fetchMovieHandler} = useHttp();

    const fetchData = () => {
        const requestMoviesConfig = {url: 'https://react-http-aa7a6-default-rtdb.firebaseio.com/meals.json'};
        const transformMeals = (mealsObj) => {
            const loadMeals = [];
            for (const key in (mealsObj)) {
                loadMeals.push({
                    id: key,
                    title: mealsObj[key].title,
                    openingText: mealsObj[key].openingText,
                    releaseDate: mealsObj[key].releaseDate
                });
            }
            setMeals(loadMeals);
        };
        fetchMovieHandler(requestMoviesConfig, transformMeals);
    };

    useEffect(() => {
        fetchData();
    }, []);

    const addMeals = ()=>{
        const addMoviesConfig = {
            url: 'https://react-http-aa7a6-default-rtdb.firebaseio.com/meal.json',
            method: 'POST',
            body: meal,
            headers: {
                'Content-Type': 'application/json'
            }
        };
        fetchMovieHandler(addMoviesConfig, () => {
            props.onNewMovie();
        });
    };

    const mealsList = meals.map(meal => {
        return <MealItem
            key={meal.id}
            id={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
        />
    });

    return (
        <section className={classes.meals}>
            <Card>
                <ul>{mealsList}</ul>
                <button onClick={addMeals}></button>
            </Card>
        </section>

    )

};

export default AvailableMeals;
